import 'package:firebase_database/firebase_database.dart';

class Meal {
   num id;
   String name;
  String urlImage;
  int amount;
  double price;
   Map<String, dynamic> toJson() {
     return {
       'id': id,
       'name': name,
       'urlImage': urlImage,
       'amount': amount,
       'price': price,
     };
   }

   Meal(this.id, this.name, this.urlImage,this.amount,this.price);
   // Create a Meal object from a JSON map
   // Create a Meal object from a JSON map
   factory Meal.fromSnapshot(DataSnapshot dataSnapshot) {
     var data = dataSnapshot.value as Map?;


     return Meal(
       data?['id'],
       data?['name'],
       data?['urlImage'],
       data?['amount'],
       data?['price'],
     );
   }
}

